import 'package:dilevery_app/core/class/crud.dart';import 'package:dilevery_app/core/constant/name_route.dart';import 'package:dilevery_app/core/functions/printing.dart';import 'package:dilevery_app/core/functions/status_request.dart';import 'package:dilevery_app/core/services/shared_pred.dart';import 'package:dilevery_app/data/data_source/remote_data/auth/sign_in.dart';import 'package:flutter/cupertino.dart';import 'package:get/get.dart';class SignInController extends GetxController {  TextEditingController email = TextEditingController();  TextEditingController pass = TextEditingController();  Sign_in_Data sign_in_data = Sign_in_Data(Crud());  StatusRequest? statusRequest;  sign_in() async {    statusRequest = StatusRequest.loading;    update();    await sign_in_data.Sign_in(email.text, pass.text).then((value) async {      statusRequest = StatusRequest.success;      update();      if (value['status'] == 'success') {        await Shared_pref.sharedPreferences!.setString(            'delivery_id', value['data'][0]['delivery_id'].toString());        await Shared_pref.sharedPreferences!.setString(            'delivery_email', value['data'][0]['delivery_email'].toString());        await Shared_pref.sharedPreferences!.setString(            'delivery_name', value['data'][0]['delivery_name'].toString());        await Shared_pref.sharedPreferences!.setString(            'delivery_phone', value['data'][0]['delivery_phone'].toString());        if (value['data'][0]['delivery_approve'].toString() == '0') {          Get.toNamed(AppRouteName.verify_code);        } else {          Get.toNamed(AppRouteName.home);        }      } else {        Get.snackbar('Failure', 'Try Again');      }    });  }}