import 'package:dilevery_app/core/class/crud.dart';import 'package:dilevery_app/core/constant/name_route.dart';import 'package:dilevery_app/core/functions/printing.dart';import 'package:dilevery_app/core/services/shared_pred.dart';import 'package:dilevery_app/data/data_source/remote_data/auth/check_email.dart';import 'package:dilevery_app/data/data_source/remote_data/auth/verify_code.dart';import 'package:flutter/cupertino.dart';import 'package:get/get.dart';class VerifyCodeController extends GetxController {  CheckEmailData checkEmailData = CheckEmailData(Crud());  VerifyCodeData verifyCodeData = VerifyCodeData(Crud());  TextEditingController verify = TextEditingController();  String? email =      Shared_pref.sharedPreferences!.getString('delivery_email');  bool send = false;  send_code() async {    try {      await checkEmailData.check_email(email!).then((val) {        send = true;        update();        Get.snackbar('Send Verify Code Is Done', '');      });    } catch (e) {      space_print(e);      Get.snackbar('Send Verify Is Failure', '');    }  }  verify_code() async {    await verifyCodeData.verify_code(email!, verify.text).then((val) {      Get.snackbar('Done', '');      Get.toNamed(AppRouteName.home);    });  }  @override  void onInit() async {    await send_code();    super.onInit();  }}