import 'package:dilevery_app/controller/home/aceepted_controller.dart';import 'package:dilevery_app/controller/home/orders_controller.dart';import 'package:dilevery_app/view/screens/home/accepted_orders_screen.dart';import 'package:dilevery_app/view/screens/home/orders_screen.dart';import 'package:dilevery_app/view/screens/home/settings_screen.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:animated_notch_bottom_bar/animated_notch_bottom_bar/animated_notch_bottom_bar.dart';class HomeController extends GetxController {  final pageController = PageController(initialPage: 0);  final List<Widget> bottomBarPages = [    Orders_pending_screen(),    AcceptedOrders(),    Setting_screen(),  ];  int maxCount = 5;  List<String> title_app_bar = [    'Orders available',    'Approved',    'Settings',  ];  List<BottomBarItem> icons = [    BottomBarItem(      inActiveItem: Image.asset('assets/icons/shopping.png'),      activeItem: Image.asset('assets/icons/shopping.png'),      itemLabel: 'Orders',    ),    BottomBarItem(      inActiveItem: Image.asset('assets/icons/delivery.png'),      activeItem: Image.asset('assets/icons/delivery.png'),      itemLabel: 'Accepted',    ),    BottomBarItem(      inActiveItem: Image.asset('assets/icons/setting.png'),      activeItem: Image.asset('assets/icons/setting.png'),      itemLabel: 'Settings',    ),  ];  int current_index = 0;  change_page(index) {    current_index = index;    if (index == 0) {      Orders_controller orders_controller = Get.put(Orders_controller());      orders_controller.get_orders_pending();    }    if (index == 1) {      AcceptedController acceptedController = Get.put(AcceptedController());      acceptedController.get_orders_accepted();    }    pageController.animateToPage(      index,      duration: Duration(milliseconds: 200),      curve: Curves.bounceInOut,    );    update();  }}