import 'package:dilevery_app/core/class/crud.dart';import 'package:dilevery_app/core/constant/AppLink.dart';import 'package:dilevery_app/core/functions/printing.dart';import 'package:dilevery_app/core/functions/status_request.dart';import 'package:http/http.dart' as http;class CheckEmailData {  Crud crud;  CheckEmailData(this.crud);  check_email(String email) async {    try {      return await crud.postData(AppLink.check_email, {        'delivery_email': email,      }).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Exception();    }  }}