import 'package:dilevery_app/core/class/crud.dart';import 'package:dilevery_app/core/constant/AppLink.dart';import 'package:dilevery_app/core/functions/printing.dart';import 'package:dilevery_app/core/functions/status_request.dart';import 'package:http/http.dart' as http;class VerifyCodeData {  Crud crud;  VerifyCodeData(this.crud);  verify_code(String email, String code) async {    try {      return await crud.postData(AppLink.verify_code, {        'email': email,        'verify_code': code,      }).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }}