import 'package:dilevery_app/core/class/crud.dart';import 'package:dilevery_app/core/constant/AppLink.dart';import 'package:dilevery_app/core/functions/printing.dart';import 'package:dilevery_app/core/functions/status_request.dart';import 'package:http/http.dart' as http;class ApproveOrdersData {  Crud crud;  ApproveOrdersData(this.crud);  approve_order(String orderId, String deliveryId, String userId) async {    try {      return await crud.postData(AppLink.approve_order, {        'order_id': orderId,        'user_id': userId,        'delivery_id': deliveryId,      }).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      space_print(e);    }  }}