import 'package:dilevery_app/core/class/crud.dart';import 'package:dilevery_app/core/constant/AppLink.dart';import 'package:dilevery_app/core/functions/printing.dart';import 'package:dilevery_app/core/functions/status_request.dart';import 'package:http/http.dart' as http;class ArchiveOrders {  Crud crud;  ArchiveOrders(this.crud);  get_archive_order(delivery_id) async {    try {      return await crud.postData(AppLink.archive_orders, {        'delivery_id': delivery_id.toString(),      }).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }}