import 'package:dilevery_app/core/class/crud.dart';import 'package:dilevery_app/core/constant/AppLink.dart';import 'package:dilevery_app/core/functions/printing.dart';import 'package:dilevery_app/core/functions/status_request.dart';import 'package:http/http.dart' as http;class DetailsOrderData {  Crud crud;  DetailsOrderData(this.crud);  get_details_orders(String items_id,String address_id) async {    try {      return await crud.postData(AppLink.details_items_in_order, {        'items_id': items_id,        'address_id':address_id      }).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }}