import 'package:dilevery_app/core/class/crud.dart';import 'package:dilevery_app/core/constant/AppLink.dart';import 'package:dilevery_app/core/functions/printing.dart';import 'package:dilevery_app/core/functions/status_request.dart';import 'package:http/http.dart' as http;class DoneOrderDelivered {  Crud crud;  DoneOrderDelivered(this.crud);  done_order_delivered(order_id, user_id, delivery_id) async {    try {      return await crud.postData(AppLink.doneOrderDelivered, {        'order_id': order_id.toString(),        'user_id': user_id.toString(),        'delivery_id': delivery_id.toString(),      }).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }}