import 'dart:math';import 'package:dilevery_app/controller/auth/sign_in_controller.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';class SigInScreen extends StatelessWidget {  const SigInScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    Get.put(SignInController());    return Scaffold(      body: GetBuilder<SignInController>(builder: (logic) {        return SizedBox(          height: double.infinity,          width: double.infinity,          child: Center(            child: Card(              shape: RoundedRectangleBorder(                borderRadius: BorderRadius.circular(15),              ),              margin: EdgeInsets.symmetric(                horizontal: 10,              ),              child: Padding(                padding: EdgeInsets.symmetric(                  horizontal: 20,                ),                child: Column(                  mainAxisSize: MainAxisSize.min,                  children: [                    SizedBox(                      height: 20,                    ),                    TextFormField(                      controller: logic.email,                      decoration: InputDecoration(                          border: OutlineInputBorder(                        borderRadius: BorderRadius.circular(20),                      )),                    ),                    SizedBox(                      height: 20,                    ),                    TextFormField(                      controller: logic.pass,                      decoration: InputDecoration(                        border: OutlineInputBorder(                          borderSide: BorderSide(                            color: Colors.red,                          ),                          borderRadius: BorderRadius.circular(20),                        ),                      ),                    ),                    SizedBox(                      height: 30,                    ),                    ActionChip(                        onPressed: () {                          logic.sign_in();                        },                        backgroundColor: Colors.blue,                        label: Padding(                          padding:                              EdgeInsets.symmetric(horizontal: 20, vertical: 5),                          child: Text(                            'LOGIN',                            style: TextStyle(                              fontSize: 18,                            ),                          ),                        )),                    SizedBox(                      height: 20,                    ),                  ],                ),              ),            ),          ),        );      }),    );  }}