import 'package:dilevery_app/controller/auth/verify_code_controller.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';class VerifyCodeScreen extends StatelessWidget {  const VerifyCodeScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    Get.put(VerifyCodeController());    return Scaffold(      appBar: AppBar(        title: Text('Verify Code'),      ),      body: GetBuilder<VerifyCodeController>(          builder: (logic) {        return Column(          children: [            if (logic.send == true)              Text('A verification code has been sent to your account'),            SizedBox(              height: 50,            ),            TextFormField(              controller: logic.verify,              decoration: InputDecoration(                border: OutlineInputBorder(),              ),            ),            SizedBox(              height: 50,            ),            ActionChip(              onPressed: () {                logic.verify_code();              },              label: Padding(                padding: EdgeInsets.symmetric(horizontal: 20, vertical: 10),                child: Text(                  'Verify',                  style: TextStyle(                    fontSize: 20,                  ),                ),              ),            ),            Spacer(),            IconButton(              onPressed: () async {                await logic.send_code();              },              icon: Icon(Icons.refresh),            ),          ],        );      }),    );  }}