import 'package:dilevery_app/controller/home/settings_controller.dart';import 'package:dilevery_app/core/constant/name_route.dart';import 'package:dilevery_app/view/widget/home/order_card_view.dart';import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:get/get.dart';class ArchiveOrdersScreen extends StatelessWidget {  const ArchiveOrdersScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return GetBuilder<SettingsController>(builder: (logic) {      return Container(        color: Colors.white,        // height: double.infinity,        // width: double.infinity,        padding: EdgeInsets.all(10),        child: ListView.builder(          itemCount: logic.archive_orders.length,          itemBuilder: (context, index) {            return OrderCardView(              accept_done_function: () {},              details_funtion: () {                Get.toNamed(AppRouteName.details,arguments: {                  'order': logic.archive_orders[index],                });              },              in_pending: false,              order: logic.archive_orders[index],            );          },        ),      );    });  }}