import 'package:dilevery_app/controller/home/settings_controller.dart';import 'package:dilevery_app/core/constant/name_route.dart';import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:get/get.dart';class Setting_screen extends StatelessWidget {  const Setting_screen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    Get.put(SettingsController());    return GetBuilder<SettingsController>(builder: (logic) {      return Container(        height: double.infinity,        width: double.infinity,        padding: EdgeInsets.all(10),        child: Column(          children: [            Card(              color: Colors.black,              child: ListTile(                title: Text(                  'Archive Orders',                  style: GoogleFonts.abel()                      .copyWith(fontSize: 20, fontWeight: FontWeight.w500,color: Colors.white),                ),                onTap: () {                  Get.toNamed(AppRouteName.archive);                },              ),            ),          ],        ),      );    });  }}