import 'package:dilevery_app/core/constant/name_route.dart';import 'package:dilevery_app/core/model/order_model.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:jiffy/jiffy.dart';import 'package:get/get.dart';Widget OrderCardView({  required Function()? accept_done_function,  required Function()? details_funtion,  required bool in_pending,  required OrderModel order,}) {  return Card(    shape: RoundedRectangleBorder(      borderRadius: BorderRadius.circular(15),    ),    color: Colors.white60,    child: Padding(      padding: EdgeInsets.symmetric(horizontal: 15, vertical: 10),      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Row(            children: [              Text(                'Order Number : ${order.orderId.toString()}',                style: GoogleFonts.alice().copyWith(                  fontSize: 16,                ),              ),              Spacer(),              Text(                '${Jiffy(order.orderDatetime).fromNow()}',                style: TextStyle(color: Colors.black, fontSize: 15),              ),            ],          ),          SizedBox(            height: 10,          ),          Text('Order price :  ${order.orderPrice}'),          SizedBox(            height: 10,          ),          Text('Delivery price :  ${order.orderPricedelivery}'),          SizedBox(            height: 10,          ),          Text(              'Payment Method :  ${order.order_payment_method == 0 ? 'Cash on delivery' : 'Payment Card'}'),          Row(            mainAxisAlignment: MainAxisAlignment.center,            children: [                ActionChip(                  onPressed: accept_done_function,                  label: Text(in_pending ? 'Accept' : 'Delivered done',                    style: GoogleFonts.alice().copyWith(                      fontSize: 18,                      fontWeight: FontWeight.w500,                    ),),                ),              SizedBox(                width: 10,              ),              ActionChip(                onPressed: details_funtion,                label: Text(                  'Details',                  style: GoogleFonts.alice().copyWith(                    fontSize: 18,                    fontWeight: FontWeight.w500,                  ),                ),              ),            ],          ),        ],      ),    ),  );}